package newton_school;

public class oops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s=0;
		int q=0;
		int []a=new int[] {1,2,3,6,4};
		for(int i:a)
		{
			s=s+a[q];
			q++;
			System.out.println(i);
			
		}
		System.out.println(s);

	}

}
